+telegram.org.css.tg-transparency:
/* tg-transparency $ Делает фон прозрачным и сохраняет анимации */
:root {
  --color-background: transparent !important;
  --theme-background-color: transparent !important;
}
.C6IaXYew, .ThfRMqwq, #root, body {
  background-color: transparent !important;
  transition:
    background-color 0.5s ease-in-out,
    background 0.5s ease-in-out,
    border 0.5s ease-in-out,
    box-shadow 0.5s ease-in-out !important;
}
.menu-container {
  background-color: #000000 !important;
  transition:
    background-color 0.5s ease-in-out,
    background 0.5s ease-in-out,
    border 0.5s ease-in-out,
    box-shadow 0.5s ease-in-out !important;
}
.settings-main-menu {
  border: none !important;
  box-shadow: none !important;
}

+telegram.org.css.tg-no-background:
/* tg-no-background $ Убирает обои чата для полной прозрачности */
body, html, html.theme-dark, #root, html.theme-dark .ThfRMqwq::before, html.theme-dark .C6IaXYew:not(.K1xhz7U5):not(.CwoXMwaR)::after {
  background-image: none !important;
  background-color: transparent !important;
}
